package ksmart.ks48team02.seller.controller.calculate;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;@Controller("sellerCalculateController")@RequestMapping("/seller/calcula")public class CalculateController {    @GetMapping(value = {"","/"})    public String sellerCalculatePage(Model model){        model.addAttribute("title","판매자 : 통계 대시보드");        model.addAttribute("contentsTitle","Today 통계 대시보드");        model.addAttribute("contentsSubTitle","오늘의 스토어 매출 통계를 확인하세요");        return "seller/calculate/main";    }    @GetMapping("/date")    public String sellerCalculateDate(Model model){        model.addAttribute("title","판매자 : 일일 매출 통계");        model.addAttribute("contentsTitle","일일 매출 통계");        model.addAttribute("contentsSubTitle","일일 스토어 매출 통계");        return "seller/calculate/dateCalcul";    }    @GetMapping("/month")    public String sellerCalculateMonth(Model model){        model.addAttribute("title","판매자 : 월 매출 통계");        model.addAttribute("contentsTitle","월별 매출 통계");        model.addAttribute("contentsSubTitle","월별 스토어 매출 통계");        return "seller/calculate/monthCalcul";    }    @GetMapping("/year")    public String sellerCalculateYear(Model model){        model.addAttribute("title","판매자 : 연 매출 통계");        model.addAttribute("contentsTitle","연 매출 통계");        model.addAttribute("contentsSubTitle","연 스토어 매출 통계");        return "seller/calculate/yearCalcul";    }    @GetMapping("/project")    public String sellerCalculateProject(Model model){        model.addAttribute("title","판매자 : 프로젝트 별 매출 통계");        model.addAttribute("contentsTitle","프로젝트 별 매출 통계");        model.addAttribute("contentsSubTitle","펀딩 실패 프로젝트는 통계 대상이 아닙니다");        return "seller/calculate/projectCalcul";    }}